"use strict";var mark=new Vue({el:"#mark",data:{mark:1,review:""},methods:{changeMark:function(a){this.mark=a},overmark:function(a){for(var e=document.querySelectorAll(".fa-star"),s=0;s<a;s++){var r=e[s].className.split(" ");r.shift(),r.unshift("fas"),e[s].className=r.join(" ")}for(var t=5;a<t;t--){var i=e[t-1].className.split(" ");i.shift(),i.unshift("far"),e[t-1].className=i.join(" ")}},restoreMark:function(){for(var a=document.querySelectorAll(".fa-star"),e=0;e<this.mark;e++){var s=a[e].className.split(" ");s.shift(),s.unshift("fas"),a[e].className=s.join(" ")}for(var r=5;r>this.mark;r--){var t=a[r-1].className.split(" ");t.shift(),t.unshift("far"),a[r-1].className=t.join(" ")}},buy:function(){fetch("/addMark",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({gameId:parseInt(document.querySelector("#game").dataset.gameid),mark:this.mark,review:this.review})}).then(function(){window.location.href="/purchases"})}}});